import React, { useState, useEffect } from 'react'
import axios from 'axios'

const Display = () => {
    const [products, setProducts] = useState({})

    const getAll = async () => {
        const response = await axios.get(`http://localhost:8000/api/all/products`)
        return response.data
    }

    useEffect(() => {
        getAll()
            .then(setProducts)
    }, [products])

    return (
        <div className='d-flex flex-column' >
            <h1 className='ms-5'>Products:</h1>
            <table className='table table-dark table-striped w-50 m-auto'>
                <thead>
                    <tr>
                        <td>Product Name:</td>
                        <td>Actions:</td>
                    </tr>
                </thead>
                <tbody>
                    {products.length > 0 &&
                        products.map((product, i) => {
                            return (
                                <tr key={i}>
                                    <td><a href={`/product/${product._id}`} className='text-decoration-underline text-light fs-3'>{product.Title}</a> </td>
                                    <td className='fs-5'><p className=''></p> | <p></p></td>
                                </tr>
                                // <div  className='container-sm flex-row d-flex justify-content-center'>
                                //     
                                //     
                                // </div>
                            )
                        })
                    }
                </tbody>
            </table>
        </div>
    )
}

export default Display
