import React, {useState, useEffect} from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios'


const Edit = () => {
    const [product, setProduct] = useState({})
    const { id } = useParams()

    const getInfo = async () => {
        const response = await axios.get(`http://localhost:8000/api/product/${id}`)
        return response.data
    }

    useEffect(() => {
        getInfo()
            .then(setProduct)
            console.log(product, "product")
    }, [product])

    // const handleSubmit = (e) =>{
    //     e.preventDefault();
    //     console.log(product)
    //     axios.post(`http://localhost:8000/api/create/product`, {
    //         Title: product.title,
    //         Price: product.price,
    //         Desc: product.desc
    //     })
    //         .then(res => console.log(res.data))
    //         .catch(err => console.log(err))
    // }

    // const handleChange = (e) =>{
    //     setProduct({...product, [e.target.name]: e.target.value})
    // }


  return (
    <div>
        <form className='bg-dark text-center pb-3' >
                <h1>Product Manager</h1>
                <div className='mb-3 w-25 text-center m-auto'>
                    <label className='form-label' >Title:</label>
                    <input name='title' className='form-control'  value={product.Title} type="text"></input>
                </div>
                <div className='mb-3 w-25 text-center m-auto'>
                    <label className='form-label' >Price:</label>
                    <input name='price' className='form-control'  value={product.Price} placeholder='Enter Price:' type="number" />
                </div>
                <div className='mb-3 w-25 text-center m-auto'>
                    <label className='form-label'>Description:</label>
                    <textarea name='desc' className="form-control" placeholder="Type Something About your product"  value={product.Desc} ></textarea>
                </div>
                <input type="submit" value="Submit" className="btn btn-outline-primary" />
            </form>
    </div>
  )
}

export default Edit
