import React, { useState, useEffect } from 'react'
import axios from 'axios'

const Display = () => {
    const [products, setProducts] = useState({})

    const getAll = async () => {
        const response = await axios.get(`http://localhost:8000/api/all/products`)
        return response.data
    }

    useEffect(() => {
        getAll()
            .then(setProducts)
    }, [products])

    return (
        <div className='d-flex flex-column' >
            <h1 className='ms-5'>Products:</h1>
            {products.length > 0 &&
                products.map((product, i) => {
                    return (
                        <div key={i} className='container-sm flex-row d-flex justify-content-center'>
                            <a href={`/product/${product._id}`} className='text-decoration-none text-light fs-3'>{product.Title}</a>
                            <p className='ms-5 text-center justify-content-center'>yo</p>
                        </div>
                    )
                })
            }
        </div>
    )
}

export default Display
